declare module '*.css';
declare module '*.less';
declare module '*.png';
declare module '*.svg' {
  export function ReactComponent(props: React.SVGProps<SVGSVGElement>): React.ReactElement;
  const url: string;
  export default url;
}

// global variables
declare const APP_NAME: string;
declare const APP_ENV: 'production' | 'testing' | 'local';
declare const API_URL: string;
